import React, { useState, useEffect } from 'react';
import { statisticsAPI } from '../../api/api';
import { Line, Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';
import Layout from '../Layout/Layout';
import './StatisticsDashboard.css';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const StatisticsDashboard = () => {
  const [statistics, setStatistics] = useState(null);
  const [rating, setRating] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadStatistics();
    loadRating();
  }, []);

  const loadStatistics = async () => {
    try {
      const response = await statisticsAPI.getUserStatistics();
      setStatistics(response.data);
    } catch (error) {
      console.error('Error loading statistics:', error);
    } finally {
      setLoading(false);
    }
  };

  const loadRating = async () => {
    try {
      const response = await statisticsAPI.getUserRating();
      setRating(response.data);
    } catch (error) {
      console.error('Error loading rating:', error);
    }
  };

  if (loading) {
    return <div className="statistics-loading">–ó–∞–≥—Ä—É–∑–∫–∞ —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫–∏...</div>;
  }

  if (!statistics) {
    return <div className="statistics-error">–û—à–∏–±–∫–∞ –∑–∞–≥—Ä—É–∑–∫–∏ —Å—Ç–∞—Ç–∏—Å—Ç–∏–∫–∏</div>;
  }

  // –î–∞–Ω–Ω—ã–µ –¥–ª—è –≥—Ä–∞—Ñ–∏–∫–∞ –∞–∫—Ç–∏–≤–Ω–æ—Å—Ç–∏ (–∑–∞–≥–ª—É—à–∫–∞ - –≤ —Ä–µ–∞–ª—å–Ω–æ—Å—Ç–∏ –Ω—É–∂–Ω—ã –¥–∞–Ω–Ω—ã–µ –ø–æ –¥–Ω—è–º)
  const activityData = {
    labels: ['–ü–Ω', '–í—Ç', '–°—Ä', '–ß—Ç', '–ü—Ç', '–°–±', '–í—Å'],
    datasets: [
      {
        label: '–ó–∞–º–µ—Ç–æ–∫ —Å–æ–∑–¥–∞–Ω–æ',
        data: [5, 8, 12, 6, 10, 7, 9],
        borderColor: 'var(--primary-color)',
        backgroundColor: 'rgba(123, 196, 127, 0.1)',
        tension: 0.4,
      },
    ],
  };

  const speedData = {
    labels: ['–°—Ä–µ–¥–Ω—è—è', '–ú–∞–∫—Å–∏–º–∞–ª—å–Ω–∞—è', '–¢–µ–∫—É—â–∞—è'],
    datasets: [
      {
        label: '–°–∫–æ—Ä–æ—Å—Ç—å –ø–µ—á–∞—Ç–∏ (WPM)',
        data: [
          statistics.typing_speed_wpm || 0,
          statistics.typing_speed_wpm * 1.5 || 0,
          statistics.typing_speed_wpm || 0,
        ],
        backgroundColor: [
          'var(--primary-color)',
          'var(--accent-color)',
          'var(--primary-color)',
        ],
      },
    ],
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        display: true,
        position: 'top',
      },
    },
    scales: {
      y: {
        beginAtZero: true,
      },
    },
  };

  return (
    <Layout>
      <div className="statistics-dashboard">
        <div className="statistics-header">
          <h2>–°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ –∞–∫—Ç–∏–≤–Ω–æ—Å—Ç–∏</h2>
        </div>

      <div className="statistics-grid">
        <div className="stat-card">
          <div className="stat-icon">üìù</div>
          <div className="stat-content">
            <div className="stat-value">{statistics.total_notes || 0}</div>
            <div className="stat-label">–í—Å–µ–≥–æ –∑–∞–º–µ—Ç–æ–∫</div>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">‚å®Ô∏è</div>
          <div className="stat-content">
            <div className="stat-value">{Math.round(statistics.typing_speed_wpm || 0)}</div>
            <div className="stat-label">–°–∫–æ—Ä–æ—Å—Ç—å (WPM)</div>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">üî•</div>
          <div className="stat-content">
            <div className="stat-value">{statistics.streak_days || 0}</div>
            <div className="stat-label">–î–Ω–µ–π –ø–æ–¥—Ä—è–¥</div>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">‚≠ê</div>
          <div className="stat-content">
            <div className="stat-value">{Math.round(statistics.rating_score || 0)}</div>
            <div className="stat-label">–†–µ–π—Ç–∏–Ω–≥</div>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">üìä</div>
          <div className="stat-content">
            <div className="stat-value">{statistics.level || 1}</div>
            <div className="stat-label">–£—Ä–æ–≤–µ–Ω—å</div>
          </div>
        </div>

        <div className="stat-card">
          <div className="stat-icon">üíé</div>
          <div className="stat-content">
            <div className="stat-value">{statistics.experience_points || 0}</div>
            <div className="stat-label">–û–ø—ã—Ç</div>
          </div>
        </div>
      </div>

      <div className="statistics-charts">
        <div className="chart-container">
          <h3>–ê–∫—Ç–∏–≤–Ω–æ—Å—Ç—å –ø–æ –¥–Ω—è–º</h3>
          <Line data={activityData} options={options} />
        </div>

        <div className="chart-container">
          <h3>–°–∫–æ—Ä–æ—Å—Ç—å –ø–µ—á–∞—Ç–∏</h3>
          <Bar data={speedData} options={options} />
        </div>
      </div>

      {rating.length > 0 && (
        <div className="rating-section">
          <h3>–†–µ–π—Ç–∏–Ω–≥ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π</h3>
          <div className="rating-list">
            {rating.slice(0, 10).map((user, index) => (
              <div key={user.user_id} className="rating-item">
                <span className="rating-rank">#{user.rank}</span>
                <span className="rating-username">{user.username}</span>
                <span className="rating-score">{Math.round(user.rating)}</span>
              </div>
            ))}
          </div>
        </div>
      )}
      </div>
    </Layout>
  );
};

export default StatisticsDashboard;
