import React, { useRef, useEffect, useState, useCallback } from 'react';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import './RichTextEditor.css';

const RichTextEditor = ({ value, onChange, placeholder = '', autoFocus = false }) => {
  const quillRef = useRef(null);
  const [wordCount, setWordCount] = useState(0);
  const [charCount, setCharCount] = useState(0);

  const modules = {
    toolbar: [
      [{ 'font': [
        '', 
        'Times New Roman', 
        'Arial', 
        'Georgia', 
        'Verdana', 
        'Courier New', 
        'Comic Sans MS',
        'Roboto',
        'Open Sans',
        'Lato',
        'Montserrat',
        'Poppins',
        'Raleway',
        'Ubuntu',
        'Playfair Display',
        'Merriweather',
        'Source Sans Pro',
        'Oswald',
        'Lora',
        'PT Sans',
        'Roboto Slab',
        'Nunito',
        'Crimson Text',
        'Dancing Script',
        'Pacifico',
        'Indie Flower',
        'Shadows Into Light',
        'Amatic SC',
        'Bangers',
        'Lobster',
        'Righteous',
        'Satisfy',
        'Kalam',
        'Caveat',
        'Permanent Marker',
        'Fredoka One',
        'Bebas Neue',
        'Anton',
        'Fjalla One',
        'Russo One',
        'Inter',
        'Work Sans',
        'DM Sans',
        'Manrope',
        'Space Grotesk',
        'Plus Jakarta Sans',
        'Outfit',
        'Sora',
        'Epilogue',
        'Figtree',
        'Cabinet Grotesk',
        'Satoshi',
        'Clash Display',
        'General Sans',
        'Switzer',
        'Chillax',
        'Sentient',
        'Fraunces',
        'Recoleta',
        'DM Serif Display',
        'Bodoni Moda',
        'Cormorant Garamond',
        'Libre Baskerville',
        'Cinzel',
        'Playfair Display SC',
        'Abril Fatface',
        'Bungee',
        'Bungee Shade',
        'Creepster',
        'Fascinate',
        'Fascinate Inline',
        'Monoton',
        'Rubik Glitch',
        'Rubik Moonrocks',
        'Titan One',
        'Wallpoet',
        'Zilla Slab Highlight',
        'Alegreya',
        'Alegreya SC',
        'Crimson Pro',
        'EB Garamond',
        'Libre Caslon Text',
        'Lora',
        'Old Standard TT',
        'PT Serif',
        'Source Serif Pro',
        'Vollkorn',
        'Yeseva One',
        'Arvo',
        'Bitter',
        'Domine',
        'Lusitana',
        'Spectral',
        'Taviraj',
        'Trirong',
        'Zilla Slab'
      ] }],
      [{ 'header': [1, 2, 3, false] }],
      [{ 'size': ['small', false, 'large', 'huge'] }],
      ['bold', 'italic', 'underline', 'strike'],
      [{ 'color': [] }, { 'background': [] }],
      [{ 'list': 'ordered'}, { 'list': 'bullet' }],
      [{ 'align': [] }],
      ['link', 'image'],
      ['clean']
    ],
  };

  const formats = [
    'font',
    'header',
    'bold', 'italic', 'underline', 'strike',
    'color', 'background',
    'list', 'bullet',
    'align',
    'link', 'image'
  ];

  const handleChange = useCallback((content, delta, source, editor) => {
    const text = editor.getText();
    const words = text.trim().split(/\s+/).filter(word => word.length > 0);
    setWordCount(words.length);
    setCharCount(text.length);
    onChange(content);
  }, [onChange]);

  useEffect(() => {
    if (value) {
      const text = quillRef.current?.getEditor()?.getText() || '';
      const words = text.trim().split(/\s+/).filter(word => word.length > 0);
      setWordCount(words.length);
      setCharCount(text.length);
    }
  }, [value]);

  // Auto-focus при монтировании компонента
  useEffect(() => {
    if (autoFocus && quillRef.current) {
      const editor = quillRef.current.getEditor();
      if (editor) {
        // Небольшая задержка для гарантии, что редактор полностью инициализирован
        setTimeout(() => {
          editor.focus();
        }, 100);
      }
    }
  }, [autoFocus]);

  return (
    <div className="rich-text-editor">
      <ReactQuill
        ref={quillRef}
        theme="snow"
        value={value || ''}
        onChange={handleChange}
        modules={modules}
        formats={formats}
        placeholder={placeholder}
        className="quill-editor"
      />
      <div className="editor-footer">
        <div className="editor-stats">
          <span>{wordCount} слов</span>
          <span>{charCount} символов</span>
        </div>
      </div>
    </div>
  );
};

export default RichTextEditor;
