import React from 'react';
import NoteCard from './NoteCard';
import './NotesGrid.css';

const NotesGrid = ({ notes, loading, onEdit, onDelete, onPin }) => {
  if (loading) {
    return (
      <div className="loading-container">
        <div className="loading-spinner">–ó–∞–≥—Ä—É–∑–∫–∞...</div>
