import React, { useState, useEffect } from 'react';
import toast from 'react-hot-toast';
import { useAuth } from '../contexts/AuthContext';
import Header from './Header/Header';
import Sidebar from './Sidebar/Sidebar';
import NotesGrid from './Notes/NotesGrid';
import NoteEditor from './Notes/NoteEditor';
import TemplatesSection from './Templates/TemplatesSection';
import DeleteModal from './Modal/DeleteModal';
import { notesAPI, foldersAPI, tagsAPI } from '../api/api';
import './NotesApp.css';

const NotesApp = () => {
  const { user, logout, loading: authLoading } = useAuth();
  const [notes, setNotes] = useState([]);
  const [folders, setFolders] = useState([]);
  const [tags, setTags] = useState([]);
  const [selectedNote, setSelectedNote] = useState(null);
  const [selectedFolder, setSelectedFolder] = useState(null);
  const [selectedTags, setSelectedTags] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [showEditor, setShowEditor] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [deleteModal, setDeleteModal] = useState({ isOpen: false, noteId: null });

  // Не загружаем данные, пока не загружен пользователь
  useEffect(() => {
    if (!authLoading && user) {
      loadData();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [authLoading, user]);

  useEffect(() => {
    if (!authLoading && user) {
      loadNotes();
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [selectedFolder, selectedTags, searchQuery, authLoading, user]);

  const loadData = async () => {
    try {
      setError(null);
      const [foldersRes, tagsRes] = await Promise.all([
        foldersAPI.getAll(),
        tagsAPI.getAll(),
      ]);
      setFolders(Array.isArray(foldersRes.data) ? foldersRes.data : []);
      setTags(Array.isArray(tagsRes.data) ? tagsRes.data : []);
    } catch (error) {
      console.error('Error loading data:', error);
      setFolders([]);
      setTags([]);
      if (error.response?.status === 401) {
        setError('Сессия истекла. Пожалуйста, войдите снова.');
      }
    }
  };

  const loadNotes = async () => {
    try {
      setLoading(true);
      setError(null);
      const params = {};
      if (selectedFolder) params.folder = selectedFolder;
      if (selectedTags.length > 0) params.tags = selectedTags;
      if (searchQuery) params.search = searchQuery;

      const response = await notesAPI.getAll(params);
      const notesData = response.data.results || response.data || [];
      setNotes(Array.isArray(notesData) ? notesData : []);
    } catch (error) {
      console.error('Error loading notes:', error);
      setNotes([]);
      if (error.response?.status === 401) {
        setError('Сессия истекла. Пожалуйста, войдите снова.');
      } else {
        setError('Ошибка загрузки заметок. Попробуйте обновить страницу.');
      }
    } finally {
      setLoading(false);
    }
  };

  const handleCreateNote = async (noteData = null) => {
    if (noteData) {
      // Если передан noteData (из шаблона), создаем заметку
      if (noteData.formData || noteData.template) {
        // Это данные из TemplateBuilder
        try {
          const newNote = await notesAPI.create({
            title: noteData.title || 'Новая заметка',
            content: noteData.content || '',
            template: noteData.template
          });
          toast.success('Заметка создана из шаблона');
          await loadNotes();
          // Открываем созданную заметку в редакторе
          if (newNote && newNote.data) {
            setSelectedNote(newNote.data);
            setShowEditor(true);
          }
        } catch (error) {
          console.error('Error creating note from template:', error);
          toast.error(error.response?.data?.error || 'Ошибка при создании заметки из шаблона');
        }
      } else {
        // Это существующая заметка
        setSelectedNote(noteData);
        setShowEditor(true);
      }
    } else {
      // Создаем новую заметку
      setSelectedNote(null);
      setShowEditor(true);
    }
  };

  const handleEditNote = (note) => {
    setSelectedNote(note);
    setShowEditor(true);
  };

  const handleSaveNote = async (noteData) => {
    try {
      if (selectedNote && selectedNote.id) {
        await notesAPI.update(selectedNote.id, noteData);
        toast.success('Заметка обновлена');
      } else {
        await notesAPI.create(noteData);
        toast.success('Заметка создана');
      }
      await loadNotes();
      setShowEditor(false);
      setSelectedNote(null);
    } catch (error) {
      console.error('Error saving note:', error);
      toast.error('Ошибка при сохранении заметки');
      throw error;
    }
  };

  const handleDeleteNote = (noteId) => {
    setDeleteModal({ isOpen: true, noteId });
  };

  const confirmDelete = async () => {
    if (deleteModal.noteId) {
      try {
        await notesAPI.delete(deleteModal.noteId);
        await loadNotes();
        if (selectedNote?.id === deleteModal.noteId) {
          setShowEditor(false);
          setSelectedNote(null);
        }
        setDeleteModal({ isOpen: false, noteId: null });
      } catch (error) {
        console.error('Error deleting note:', error);
        setDeleteModal({ isOpen: false, noteId: null });
      }
    }
  };

  const handlePinNote = async (noteId) => {
    try {
      await notesAPI.pin(noteId);
      const note = notes.find(n => n.id === noteId);
      toast.success(note?.is_pinned ? 'Заметка откреплена' : 'Заметка закреплена');
      await loadNotes();
    } catch (error) {
      console.error('Error pinning note:', error);
      toast.error('Ошибка при изменении статуса заметки');
    }
  };

  // Показываем загрузку, если еще загружается аутентификация
  if (authLoading || !user) {
    return (
      <div style={{ 
        display: 'flex', 
        flexDirection: 'column',
        justifyContent: 'center', 
        alignItems: 'center', 
        height: '100vh',
        fontSize: '17px',
        color: 'var(--text-secondary)',
        background: 'var(--bg-color)'
      }}>
        <div style={{
          width: '40px',
          height: '40px',
          border: '3px solid var(--border-color)',
          borderTopColor: 'var(--primary-color)',
          borderRadius: '50%',
          animation: 'spin 1s linear infinite',
          marginBottom: '16px'
        }}></div>
        <style>{`
          @keyframes spin {
            to { transform: rotate(360deg); }
          }
        `}</style>
        Загрузка...
      </div>
    );
  }

  return (
    <div className="notes-app">
      <Header
        user={user}
        onLogout={logout}
        searchQuery={searchQuery}
        onSearchChange={setSearchQuery}
        onCreateNote={handleCreateNote}
      />
      <div className="notes-app-content">
        <Sidebar
          folders={folders}
          tags={tags}
          selectedFolder={selectedFolder}
          selectedTags={selectedTags}
          onFolderSelect={setSelectedFolder}
          onTagSelect={setSelectedTags}
          onFolderCreate={loadData}
          onTagCreate={loadData}
        />
        <div className="notes-main">
          {error && (
            <div className="error-banner">
              <span>{error}</span>
              <button onClick={() => { setError(null); loadNotes(); }}>Обновить</button>
            </div>
          )}
          {showEditor ? (
            <NoteEditor
              note={selectedNote}
              folders={folders}
              tags={tags}
              onSave={handleSaveNote}
              onCancel={() => {
                setShowEditor(false);
                setSelectedNote(null);
              }}
            />
          ) : (
            <>
              <NotesGrid
                notes={notes}
                loading={loading}
                onEdit={handleEditNote}
                onDelete={handleDeleteNote}
                onPin={handlePinNote}
              />
              <TemplatesSection onTemplateSelect={handleCreateNote} />
            </>
          )}
        </div>
      </div>
      <DeleteModal
        isOpen={deleteModal.isOpen}
        onClose={() => setDeleteModal({ isOpen: false, noteId: null })}
        onConfirm={confirmDelete}
        title="Удаление заметки"
        message="Вы уверены, что хотите удалить эту заметку? Это действие нельзя отменить."
      />
    </div>
  );
};

export default NotesApp;
