import React, { useState } from 'react';
import { foldersAPI, tagsAPI } from '../../api/api';
import FolderForm from './FolderForm';
import TagForm from './TagForm';
import './Sidebar.css';

const Sidebar = ({
  folders,
  tags,
  selectedFolder,
  selectedTags,
  onFolderSelect,
  onTagSelect,
  onFolderCreate,
  onTagCreate,
}) => {
  const [showFolderForm, setShowFolderForm] = useState(false);
  const [showTagForm, setShowTagForm] = useState(false);

  const handleFolderCreate = async (folderData) => {
    try {
      const response = await foldersAPI.create(folderData);
      if (response && response.data) {
        await onFolderCreate();
        setShowFolderForm(false);
      }
    } catch (error) {
      console.error('Error creating folder:', error);
      alert(error.response?.data?.error || '–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ–∑–¥–∞–Ω–∏–∏ –ø–∞–ø–∫–∏. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â–µ —Ä–∞–∑.');
    }
  };

  const handleTagCreate = async (tagData) => {
    try {
      const response = await tagsAPI.create(tagData);
      if (response && response.data) {
        await onTagCreate();
        setShowTagForm(false);
      }
    } catch (error) {
      console.error('Error creating tag:', error);
      alert(error.response?.data?.error || '–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ–∑–¥–∞–Ω–∏–∏ —Ç–µ–≥–∞. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â–µ —Ä–∞–∑.');
    }
  };

  const toggleTag = (tagId) => {
    if (selectedTags.includes(tagId)) {
      onTagSelect(selectedTags.filter(id => id !== tagId));
    } else {
      onTagSelect([...selectedTags, tagId]);
    }
  };

  return (
    <aside className="sidebar">
      <div className="sidebar-section">
        <div className="sidebar-header">
          <h3>üìÅ –ü–∞–ø–∫–∏</h3>
          <button
            className="btn-add"
            onClick={() => setShowFolderForm(!showFolderForm)}
            title="–°–æ–∑–¥–∞—Ç—å –ø–∞–ø–∫—É"
          >
            +
          </button>
        </div>
        {showFolderForm && (
          <FolderForm
            onSubmit={handleFolderCreate}
            onCancel={() => setShowFolderForm(false)}
          />
        )}
        <div className="folder-list">
          <button
            className={`folder-item ${selectedFolder === null ? 'active' : ''}`}
            onClick={() => onFolderSelect(null)}
          >
            –í—Å–µ –∑–∞–º–µ—Ç–∫–∏
          </button>
          {Array.isArray(folders) && folders.map(folder => (
            <button
              key={folder.id}
              className={`folder-item ${selectedFolder === folder.id ? 'active' : ''}`}
              onClick={() => onFolderSelect(folder.id)}
              style={{ borderLeftColor: folder.color }}
            >
              {folder.name}
              <span className="folder-count">({folder.notes_count || 0})</span>
            </button>
          ))}
        </div>
      </div>

      <div className="sidebar-section">
        <div className="sidebar-header">
          <h3>üè∑Ô∏è –¢–µ–≥–∏</h3>
          <button
            className="btn-add"
            onClick={() => setShowTagForm(!showTagForm)}
            title="–°–æ–∑–¥–∞—Ç—å —Ç–µ–≥"
          >
            +
          </button>
        </div>
        {showTagForm && (
          <TagForm
            onSubmit={handleTagCreate}
            onCancel={() => setShowTagForm(false)}
          />
        )}
        <div className="tag-list">
          {Array.isArray(tags) && tags.map(tag => (
            <button
              key={tag.id}
              className={`tag-item ${selectedTags.includes(tag.id) ? 'active' : ''}`}
              onClick={() => toggleTag(tag.id)}
              style={{
                backgroundColor: selectedTags.includes(tag.id) ? tag.color : 'transparent',
                borderColor: tag.color,
                color: selectedTags.includes(tag.id) ? '#fff' : tag.color,
              }}
            >
              {tag.name}
              <span className="tag-count">({tag.notes_count || 0})</span>
            </button>
          ))}
        </div>
      </div>
    </aside>
  );
};

export default Sidebar;
