import React, { useState, useEffect, useRef } from 'react';
import { foldersAPI, tagsAPI } from '../../api/api';
import './NoteEditor.css';

const NoteEditor = ({ note, folders, tags, onSave, onCancel }) => {
  const [title, setTitle] = useState(note?.title || '');
  const [content, setContent] = useState(note?.content || '');
  const [selectedFolder, setSelectedFolder] = useState(note?.folder || null);
  const [selectedTags, setSelectedTags] = useState(note?.tags?.map(t => t.id) || []);
  const [saving, setSaving] = useState(false);
  const editorRef = useRef(null);

  useEffect(() => {
    if (editorRef.current) {
      editorRef.current.focus();
    }
  }, []);

  const handleFormat = (command, value = null) => {
    document.execCommand(command, false, value);
    editorRef.current.focus();
  };

  const handleSave = async () => {
    setSaving(true);
    try {
      await onSave({
        title: title.trim() || 'Без названия',
        content: editorRef.current.innerHTML,
        folder: selectedFolder || null,
        tag_ids: selectedTags,
      });
    } catch (error) {
      console.error('Error saving note:', error);
      alert('Ошибка при сохранении заметки');
    } finally {
      setSaving(false);
    }
  };

  const toggleTag = (tagId) => {
    if (selectedTags.includes(tagId)) {
      setSelectedTags(selectedTags.filter(id => id !== tagId));
    } else {
      setSelectedTags([...selectedTags, tagId]);
    }
  };

  return (
    <div className="note-editor">
      <div className="editor-header">
        <input
          type="text"
          className="editor-title"
          placeholder="Заголовок заметки"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <div className="editor-actions">
          <button className="btn-save" onClick={handleSave} disabled={saving}>
            {saving ? 'Сохранение...' : 'Сохранить'}
          </button>
          <button className="btn-cancel" onClick={onCancel}>Отмена</button>
        </div>
      </div>

      <div className="editor-toolbar">
        <button className="toolbar-btn" onClick={() => handleFormat('bold')} title="Жирный (Ctrl+B)">B</button>
        <button className="toolbar-btn" onClick={() => handleFormat('italic')} title="Курсив (Ctrl+I)">I</button>
        <button className="toolbar-btn" onClick={() => handleFormat('underline')} title="Подчеркнутый">U</button>
        <div className="toolbar-divider"></div>
        <button className="toolbar-btn" onClick={() => handleFormat('formatBlock', 'h1')} title="Заголовок 1">H1</button>
        <button className="toolbar-btn" onClick={() => handleFormat('formatBlock', 'h2')} title="Заголовок 2">H2</button>
        <button className="toolbar-btn" onClick={() => handleFormat('formatBlock', 'h3')} title="Заголовок 3">H3</button>
        <div className="toolbar-divider"></div>
        <button className="toolbar-btn" onClick={() => handleFormat('insertUnorderedList')} title="Маркированный список">•</button>
        <button className="toolbar-btn" onClick={() => handleFormat('insertOrderedList')} title="Нумерованный список">1.</button>
        <div className="toolbar-divider"></div>
        <button className="toolbar-btn" onClick={() => handleFormat('justifyLeft')} title="По левому краю">⬅</button>
        <button className="toolbar-btn" onClick={() => handleFormat('justifyCenter')} title="По центру">⬌</button>
        <button className="toolbar-btn" onClick={() => handleFormat('justifyRight')} title="По правому краю">➡</button>
      </div>

      <div
        ref={editorRef}
        className="editor-content"
        contentEditable
        dangerouslySetInnerHTML={{ __html: content }}
        onInput={(e) => setContent(e.target.innerHTML)}
        placeholder="Начните писать вашу заметку..."
      />

      <div className="editor-sidebar">
        <div className="editor-section">
          <label>Папка:</label>
          <select
            value={selectedFolder || ''}
            onChange={(e) => setSelectedFolder(e.target.value ? parseInt(e.target.value) : null)}
            className="editor-select"
          >
            <option value="">Без папки</option>
            {folders.map(folder => (
              <option key={folder.id} value={folder.id}>{folder.name}</option>
            ))}
          </select>
        </div>

        <div className="editor-section">
          <label>Теги:</label>
          <div className="tags-selector">
            {tags.map(tag => (
              <button
                key={tag.id}
                className={`tag-selector-btn ${selectedTags.includes(tag.id) ? 'active' : ''}`}
                onClick={() => toggleTag(tag.id)}
                style={{
                  backgroundColor: selectedTags.includes(tag.id) ? tag.color : 'transparent',
                  borderColor: tag.color,
                  color: selectedTags.includes(tag.id) ? '#fff' : tag.color,
                }}
              >
                {tag.name}
              </button>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default NoteEditor;
