import React, { useState } from 'react';
import './TemplateBuilder.css';

const TemplateBuilder = ({ template, onSave, onCancel }) => {
  const [formData, setFormData] = useState({});
  const [errors, setErrors] = useState({});

  const fields = template?.template_data || [];

  const handleChange = (fieldName, value) => {
    setFormData(prev => ({
      ...prev,
      [fieldName]: value
    }));
    // Очищаем ошибку при изменении
    if (errors[fieldName]) {
      setErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors[fieldName];
        return newErrors;
      });
    }
  };

  const validateField = (field, value) => {
    if (field.required && (!value || value.toString().trim() === '')) {
      return `${field.label} обязателен для заполнения`;
    }
    if (field.type === 'number' && value && isNaN(value)) {
      return `${field.label} должен быть числом`;
    }
    return null;
  };

  const validateForm = () => {
    const newErrors = {};
    fields.forEach(field => {
      const value = formData[field.name];
      const error = validateField(field, value);
      if (error) {
        newErrors[field.name] = error;
      }
    });
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      // Генерируем HTML контент из заполненных данных
      const htmlContent = generateHTMLContent(formData, fields, template);
      onSave({
        title: template.name,
        content: htmlContent,
        template: template.id,
        formData: formData
      });
    }
  };

  const renderField = (field) => {
    const value = formData[field.name] || '';
    const error = errors[field.name];

    switch (field.type) {
      case 'textarea':
        return (
          <div key={field.name} className="template-field">
            <label className="template-field-label">
              {field.label}
              {field.required && <span className="required">*</span>}
            </label>
            <textarea
              className={`template-field-input ${error ? 'error' : ''}`}
              value={value}
              onChange={(e) => handleChange(field.name, e.target.value)}
              placeholder={field.placeholder}
              rows={4}
            />
            {error && <span className="field-error">{error}</span>}
          </div>
        );

      case 'select':
        return (
          <div key={field.name} className="template-field">
            <label className="template-field-label">
              {field.label}
              {field.required && <span className="required">*</span>}
            </label>
            <select
              className={`template-field-input ${error ? 'error' : ''}`}
              value={value}
              onChange={(e) => handleChange(field.name, e.target.value)}
            >
              <option value="">{field.placeholder}</option>
              {field.options?.map((option, idx) => (
                <option key={idx} value={option}>{option}</option>
              ))}
            </select>
            {error && <span className="field-error">{error}</span>}
          </div>
        );

      case 'checkbox':
        return (
          <div key={field.name} className="template-field checkbox-field">
            <label className="template-field-label checkbox-label">
              <input
                type="checkbox"
                checked={value === true || value === 'true'}
                onChange={(e) => handleChange(field.name, e.target.checked)}
                className="template-checkbox"
              />
              <span>{field.label}</span>
            </label>
            {error && <span className="field-error">{error}</span>}
          </div>
        );

      case 'date':
        return (
          <div key={field.name} className="template-field">
            <label className="template-field-label">
              {field.label}
              {field.required && <span className="required">*</span>}
            </label>
            <input
              type="date"
              className={`template-field-input ${error ? 'error' : ''}`}
              value={value}
              onChange={(e) => handleChange(field.name, e.target.value)}
              placeholder={field.placeholder}
            />
            {error && <span className="field-error">{error}</span>}
          </div>
        );

      case 'number':
        return (
          <div key={field.name} className="template-field">
            <label className="template-field-label">
              {field.label}
              {field.required && <span className="required">*</span>}
            </label>
            <input
              type="number"
              className={`template-field-input ${error ? 'error' : ''}`}
              value={value}
              onChange={(e) => handleChange(field.name, e.target.value)}
              placeholder={field.placeholder}
            />
            {error && <span className="field-error">{error}</span>}
          </div>
        );

      default:
        return (
          <div key={field.name} className="template-field">
            <label className="template-field-label">
              {field.label}
              {field.required && <span className="required">*</span>}
            </label>
            <input
              type="text"
              className={`template-field-input ${error ? 'error' : ''}`}
              value={value}
              onChange={(e) => handleChange(field.name, e.target.value)}
              placeholder={field.placeholder}
            />
            {error && <span className="field-error">{error}</span>}
          </div>
        );
    }
  };

  if (!template) {
    return null;
  }

  return (
    <div className="template-builder">
      <div className="template-builder-header">
        <h2>{template.name}</h2>
        {template.description && (
          <p className="template-description">{template.description}</p>
        )}
      </div>

      <form onSubmit={handleSubmit} className="template-form">
        {fields.map(field => renderField(field))}

        <div className="template-form-actions">
          <button type="button" onClick={onCancel} className="btn-cancel">
            Отмена
          </button>
          <button type="submit" className="btn-save">
            Создать заметку
          </button>
        </div>
      </form>
    </div>
  );
};

// Функция для генерации HTML контента из заполненных данных
const generateHTMLContent = (formData, fields, template) => {
  const parts = [`<h1>${template.name}</h1>`];

  fields.forEach(field => {
    const value = formData[field.name];
    if (value !== undefined && value !== null && value !== '') {
      const label = field.label;

      if (field.type === 'checkbox') {
        parts.push(`<p><strong>${label}:</strong> ${value ? 'Да' : 'Нет'}</p>`);
      } else if (field.type === 'textarea') {
        parts.push(`<h3>${label}</h3>`);
        parts.push(`<p>${value.replace(/\n/g, '<br>')}</p>`);
      } else {
        parts.push(`<p><strong>${label}:</strong> ${value}</p>`);
      }
    }
  });

  return parts.join('\n');
};

export default TemplateBuilder;
