import React, { useState, memo } from 'react';
import toast from 'react-hot-toast';
import { foldersAPI, tagsAPI } from '../../api/api';
import FolderForm from './FolderForm';
import TagForm from './TagForm';
import SidebarTabs from './SidebarTabs';
import SkeletonList from '../Skeleton/SkeletonList';
import './Sidebar.css';

const Sidebar = memo(({
  folders,
  tags,
  selectedFolder,
  selectedTags,
  onFolderSelect,
  onTagSelect,
  onFolderCreate,
  onTagCreate,
  loading = false,
}) => {
  const [activeTab, setActiveTab] = useState('folders');
  const [showFolderForm, setShowFolderForm] = useState(false);
  const [showTagForm, setShowTagForm] = useState(false);

  const handleFolderCreate = async (folderData) => {
    try {
      const response = await foldersAPI.create(folderData);
      console.log('Folder creation response:', response);
      if (response && (response.data || response.status === 201)) {
        // –û–±–Ω–æ–≤–ª—è–µ–º —Å–ø–∏—Å–æ–∫ –ø–∞–ø–æ–∫
        await onFolderCreate();
        setShowFolderForm(false);
        toast.success('–ü–∞–ø–∫–∞ —Å–æ–∑–¥–∞–Ω–∞');
      } else {
        throw new Error('–ù–µ–æ–∂–∏–¥–∞–Ω–Ω—ã–π –æ—Ç–≤–µ—Ç –æ—Ç —Å–µ—Ä–≤–µ—Ä–∞');
      }
    } catch (error) {
      console.error('Error creating folder:', error);
      const errorMessage = error.response?.data?.error || 
                          error.response?.data?.detail || 
                          error.message || 
                          '–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ–∑–¥–∞–Ω–∏–∏ –ø–∞–ø–∫–∏. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â–µ —Ä–∞–∑.';
      toast.error(errorMessage);
    }
  };

  const handleTagCreate = async (tagData) => {
    try {
      const response = await tagsAPI.create(tagData);
      console.log('Tag creation response:', response);
      if (response && (response.data || response.status === 201)) {
        // –û–±–Ω–æ–≤–ª—è–µ–º —Å–ø–∏—Å–æ–∫ —Ç–µ–≥–æ–≤
        await onTagCreate();
        setShowTagForm(false);
        toast.success('–¢–µ–≥ —Å–æ–∑–¥–∞–Ω');
      } else {
        throw new Error('–ù–µ–æ–∂–∏–¥–∞–Ω–Ω—ã–π –æ—Ç–≤–µ—Ç –æ—Ç —Å–µ—Ä–≤–µ—Ä–∞');
      }
    } catch (error) {
      console.error('Error creating tag:', error);
      const errorMessage = error.response?.data?.error || 
                          error.response?.data?.detail || 
                          error.message || 
                          '–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ–∑–¥–∞–Ω–∏–∏ —Ç–µ–≥–∞. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –µ—â–µ —Ä–∞–∑.';
      toast.error(errorMessage);
    }
  };

  const toggleTag = (tagId) => {
    if (selectedTags.includes(tagId)) {
      onTagSelect(selectedTags.filter(id => id !== tagId));
    } else {
      onTagSelect([...selectedTags, tagId]);
    }
  };

  const handleContextMenu = (e, type, item) => {
    e.preventDefault();
    // TODO: –ü–æ–∫–∞–∑–∞—Ç—å –∫–æ–Ω—Ç–µ–∫—Å—Ç–Ω–æ–µ –º–µ–Ω—é
    console.log('Context menu:', type, item);
  };

  return (
    <aside className="sidebar">
      <SidebarTabs activeTab={activeTab} onTabChange={setActiveTab}>
        {activeTab === 'folders' ? (
          <div className="sidebar-section">
            <div className="sidebar-header">
              <h3>üìÅ –ü–∞–ø–∫–∏</h3>
              <button
                className="btn-add"
                onClick={() => setShowFolderForm(!showFolderForm)}
                title="–°–æ–∑–¥–∞—Ç—å –ø–∞–ø–∫—É"
              >
                +
              </button>
            </div>
            {showFolderForm && (
              <FolderForm
                onSubmit={handleFolderCreate}
                onCancel={() => setShowFolderForm(false)}
              />
            )}
            {loading ? (
              <SkeletonList count={5} />
            ) : (
              <div className="folder-list">
                <button
                  className={`folder-item ${selectedFolder === null ? 'active' : ''}`}
                  onClick={() => onFolderSelect(null)}
                >
                  üìÇ –í—Å–µ –∑–∞–º–µ—Ç–∫–∏
                </button>
                {Array.isArray(folders) && folders
                  .filter(f => f.is_favorite)
                  .map(folder => (
                    <button
                      key={folder.id}
                      className={`folder-item ${selectedFolder === folder.id ? 'active' : ''}`}
                      onClick={() => onFolderSelect(folder.id)}
                      onContextMenu={(e) => handleContextMenu(e, 'folder', folder)}
                      style={{ borderLeftColor: folder.color }}
                    >
                      {folder.is_favorite && <span>‚≠ê </span>}
                      {folder.name}
                      <span className="folder-count">({folder.notes_count || 0})</span>
                    </button>
                  ))}
                {Array.isArray(folders) && folders.filter(f => !f.is_favorite).length > 0 && (
                  <>
                    {folders
                      .filter(f => !f.is_favorite)
                      .slice(0, 5)
                      .map(folder => (
                        <button
                          key={folder.id}
                          className={`folder-item ${selectedFolder === folder.id ? 'active' : ''}`}
                          onClick={() => onFolderSelect(folder.id)}
                          onContextMenu={(e) => handleContextMenu(e, 'folder', folder)}
                          style={{ borderLeftColor: folder.color }}
                        >
                          {folder.name}
                          <span className="folder-count">({folder.notes_count || 0})</span>
                        </button>
                      ))}
                    {folders.filter(f => !f.is_favorite).length > 5 && (
                      <button className="folder-item folder-more">
                        ‚ãÆ –ü–æ–∫–∞–∑–∞—Ç—å –≤—Å–µ ({folders.filter(f => !f.is_favorite).length - 5})
                      </button>
                    )}
                  </>
                )}
              </div>
            )}
          </div>
        ) : (
          <div className="sidebar-section">
            <div className="sidebar-header">
              <h3>üè∑Ô∏è –¢–µ–≥–∏</h3>
              <button
                className="btn-add"
                onClick={() => setShowTagForm(!showTagForm)}
                title="–°–æ–∑–¥–∞—Ç—å —Ç–µ–≥"
              >
                +
              </button>
            </div>
            {showTagForm && (
              <TagForm
                onSubmit={handleTagCreate}
                onCancel={() => setShowTagForm(false)}
              />
            )}
            {loading ? (
              <SkeletonList count={5} />
            ) : (
              <div className="tag-list">
                {Array.isArray(tags) && tags
                  .sort((a, b) => (b.usage_count || 0) - (a.usage_count || 0))
                  .slice(0, 10)
                  .map(tag => (
                    <button
                      key={tag.id}
                      className={`tag-item ${selectedTags.includes(tag.id) ? 'active' : ''}`}
                      onClick={() => toggleTag(tag.id)}
                      onContextMenu={(e) => handleContextMenu(e, 'tag', tag)}
                      style={{
                        backgroundColor: selectedTags.includes(tag.id) ? tag.color : 'transparent',
                        borderColor: tag.color,
                        color: selectedTags.includes(tag.id) ? '#fff' : tag.color,
                      }}
                    >
                      {tag.name}
                      <span className="tag-count">({tag.notes_count || 0})</span>
                    </button>
                  ))}
                {Array.isArray(tags) && tags.length > 10 && (
                  <button className="tag-item tag-more">
                    ‚ãÆ –ü–æ–∫–∞–∑–∞—Ç—å –≤—Å–µ ({tags.length - 10})
                  </button>
                )}
              </div>
            )}
          </div>
        )}
      </SidebarTabs>
    </aside>
  );
});

Sidebar.displayName = 'Sidebar';

export default Sidebar;
