import React, { useState, useEffect, useRef, useCallback } from 'react';
import toast from 'react-hot-toast';
import { useKeyboardShortcuts } from '../../hooks/useKeyboardShortcuts';
import RichTextEditor from './RichTextEditor';
import './NoteEditor.css';

const NoteEditor = ({ note, folders, tags, onSave, onCancel }) => {
  const [title, setTitle] = useState(note?.title || '');
  const [content, setContent] = useState(note?.content || '');
  const [selectedFolder, setSelectedFolder] = useState(note?.folder || null);
  const [selectedTags, setSelectedTags] = useState(note?.tags?.map(t => t.id) || []);
  const [saving, setSaving] = useState(false);
  const [saveStatus, setSaveStatus] = useState('saved'); // 'saved', 'saving', 'unsaved'
  const autoSaveTimerRef = useRef(null);

  useEffect(() => {
    if (note) {
      setTitle(note.title || '');
      setContent(note.content || '');
      setSelectedFolder(note.folder || null);
      setSelectedTags(note.tags?.map(t => t.id) || []);
    }
  }, [note]);

  const handleAutoSave = useCallback(async () => {
    if (!title.trim() && !content.trim()) return;

    setSaveStatus('saving');
    try {
      await onSave({
        title: title.trim() || 'Без названия',
        content: content,
        folder: selectedFolder || null,
        tag_ids: selectedTags,
      });
      setSaveStatus('saved');
    } catch (error) {
      console.error('Error auto-saving note:', error);
      setSaveStatus('unsaved');
    }
  }, [title, content, selectedFolder, selectedTags, onSave]);

  // Автосохранение каждые 30 секунд
  useEffect(() => {
    if (autoSaveTimerRef.current) {
      clearInterval(autoSaveTimerRef.current);
    }

    if (content && title) {
      autoSaveTimerRef.current = setInterval(() => {
        handleAutoSave();
      }, 30000); // 30 секунд
    }

    return () => {
      if (autoSaveTimerRef.current) {
        clearInterval(autoSaveTimerRef.current);
      }
    };
  }, [content, title, selectedFolder, selectedTags, handleAutoSave]);

  const handleSave = useCallback(async () => {
    setSaving(true);
    setSaveStatus('saving');
    try {
      await onSave({
        title: title.trim() || 'Без названия',
        content: content,
        folder: selectedFolder || null,
        tag_ids: selectedTags,
      });
      setSaveStatus('saved');
      toast.success('Заметка сохранена');
    } catch (error) {
      console.error('Error saving note:', error);
      setSaveStatus('unsaved');
      toast.error('Ошибка при сохранении заметки');
    } finally {
      setSaving(false);
    }
  }, [title, content, selectedFolder, selectedTags, onSave]);

  // Клавиатурные сокращения для редактора
  useKeyboardShortcuts({
    'ctrl+s': (e) => {
      e.preventDefault();
      handleSave();
    },
    'escape': () => {
      onCancel();
    },
  }, [handleSave, onCancel]);

  const toggleTag = (tagId) => {
    if (selectedTags.includes(tagId)) {
      setSelectedTags(selectedTags.filter(id => id !== tagId));
    } else {
      setSelectedTags([...selectedTags, tagId]);
    }
  };

  return (
    <div className="note-editor">
      <div className="editor-header">
        <input
          type="text"
          className="editor-title"
          placeholder="Заголовок заметки"
          value={title}
          onChange={(e) => {
            setTitle(e.target.value);
            setSaveStatus('unsaved');
          }}
        />
        <div className="editor-actions">
          <div className="save-status">
            {saveStatus === 'saved' && (
              <span className="status-indicator saved">
                <svg width="16" height="16" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                  <circle cx="12" cy="12" r="10" fill="#22C55E"/>
                  <path d="M8 12L11 15L16 9" stroke="white" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                </svg>
                Сохранено
              </span>
            )}
            {saveStatus === 'saving' && (
              <span className="status-indicator saving">
                <svg width="16" height="16" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                  <circle cx="12" cy="12" r="10" fill="#EAB308"/>
                  <path d="M12 6V12L16 14" stroke="white" strokeWidth="2" strokeLinecap="round"/>
                </svg>
                Сохранение...
              </span>
            )}
            {saveStatus === 'unsaved' && (
              <span className="status-indicator unsaved">
                <svg width="16" height="16" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                  <circle cx="12" cy="12" r="10" fill="#9CA3AF"/>
                </svg>
                Не сохранено
              </span>
            )}
          </div>
          <button className="btn-save" onClick={handleSave} disabled={saving} title="Сохранить">
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M20 6L9 17L4 12" stroke="#22C55E" strokeWidth="2.5" strokeLinecap="round" strokeLinejoin="round"/>
            </svg>
          </button>
          <button className="btn-cancel" onClick={onCancel} title="Отмена">
            <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M18 6L6 18M6 6L18 18" stroke="#EF4444" strokeWidth="2.5" strokeLinecap="round" strokeLinejoin="round"/>
            </svg>
          </button>
        </div>
      </div>

      <RichTextEditor
        value={content}
        onChange={(newContent) => {
          setContent(newContent);
          setSaveStatus('unsaved');
        }}
        placeholder=""
        autoFocus={!note}
      />

      <div className="editor-sidebar">
        <div className="editor-section">
          <label>Папка:</label>
          <select
            value={selectedFolder || ''}
            onChange={(e) => setSelectedFolder(e.target.value ? parseInt(e.target.value) : null)}
            className="editor-select"
          >
            <option value="">Без папки</option>
            {folders.map(folder => (
              <option key={folder.id} value={folder.id}>{folder.name}</option>
            ))}
          </select>
        </div>

        <div className="editor-section">
          <label>Теги:</label>
          <div className="tags-selector">
            {tags.map(tag => (
              <button
                key={tag.id}
                className={`tag-selector-btn ${selectedTags.includes(tag.id) ? 'active' : ''}`}
                onClick={() => toggleTag(tag.id)}
                style={{
                  backgroundColor: selectedTags.includes(tag.id) ? tag.color : 'transparent',
                  borderColor: tag.color,
                  color: selectedTags.includes(tag.id) ? '#fff' : tag.color,
                }}
              >
                {tag.name}
              </button>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default NoteEditor;
