import React, { useState, useEffect } from 'react';
import { templatesAPI, notesAPI } from '../../api/api';
import './TemplatesSection.css';

const TemplatesSection = ({ onTemplateSelect }) => {
  const [templates, setTemplates] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    loadTemplates();
  }, []);

  const loadTemplates = async () => {
    try {
      const response = await templatesAPI.getAll();
      setTemplates(response.data);
    } catch (error) {
      console.error('Error loading templates:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleTemplateClick = async (template) => {
    try {
      const response = await notesAPI.createFromTemplate(template.id);
      // –û–±–Ω–æ–≤–ª—è–µ–º —Å–ø–∏—Å–æ–∫ –∑–∞–º–µ—Ç–æ–∫ –∏ –æ—Ç–∫—Ä—ã–≤–∞–µ–º —Ä–µ–¥–∞–∫—Ç–æ—Ä
      onTemplateSelect(response.data);
    } catch (error) {
      console.error('Error creating note from template:', error);
      alert('–û—à–∏–±–∫–∞ –ø—Ä–∏ —Å–æ–∑–¥–∞–Ω–∏–∏ –∑–∞–º–µ—Ç–∫–∏ –∏–∑ —à–∞–±–ª–æ–Ω–∞');
    }
  };

  if (loading) {
    return <div className="templates-loading">–ó–∞–≥—Ä—É–∑–∫–∞ —à–∞–±–ª–æ–Ω–æ–≤...</div>;
  }

  if (templates.length === 0) {
    return null;
  }

  return (
    <section className="templates-section">
      <h2 className="templates-title">üìã –®–∞–±–ª–æ–Ω—ã –∑–∞–º–µ—Ç–æ–∫</h2>
      <div className="templates-grid">
        {templates.map(template => (
          <div
            key={template.id}
            className="template-card"
            onClick={() => handleTemplateClick(template)}
          >
            <div className="template-icon" dangerouslySetInnerHTML={{ __html: template.icon_svg }} />
            <h3 className="template-name">{template.name}</h3>
            {template.description && (
              <p className="template-description">{template.description}</p>
            )}
          </div>
        ))}
      </div>
    </section>
  );
};

export default TemplatesSection;
