import React from 'react';
import './NoteCard.css';

const NoteCard = ({ note, onEdit, onDelete, onPin }) => {
  const formatDate = (dateString) => {
    const date = new Date(dateString);
    const now = new Date();
    const diffTime = Math.abs(now - date);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));

    if (diffDays === 1) return '–°–µ–≥–æ–¥–Ω—è';
    if (diffDays === 2) return '–í—á–µ—Ä–∞';
    if (diffDays <= 7) return `${diffDays - 1} –¥–Ω–µ–π –Ω–∞–∑–∞–¥`;

    return date.toLocaleDateString('ru-RU', {
      day: 'numeric',
      month: 'long',
      year: date.getFullYear() !== now.getFullYear() ? 'numeric' : undefined
    });
  };

  const stripHtml = (html) => {
    const tmp = document.createElement('DIV');
    tmp.innerHTML = html;
    return tmp.textContent || tmp.innerText || '';
  };

  const preview = stripHtml(note.content || '').substring(0, 150);

  return (
    <div className={`note-card ${note.is_pinned ? 'pinned' : ''}`}>
      <div className="note-card-header">
        <h3 className="note-title" onClick={() => onEdit(note)}>
          {note.is_pinned && <span className="pin-icon">üìå</span>}
          {note.title}
        </h3>
        <div className="note-actions">
          <button
            className="action-btn"
            onClick={() => onPin(note.id)}
            title={note.is_pinned ? '–û—Ç–∫—Ä–µ–ø–∏—Ç—å' : '–ó–∞–∫—Ä–µ–ø–∏—Ç—å'}
          >
            {note.is_pinned ? 'üìå' : 'üìç'}
          </button>
          <button
            className="action-btn delete"
            onClick={() => onDelete(note.id)}
            title="–£–¥–∞–ª–∏—Ç—å"
          >
            üóëÔ∏è
          </button>
        </div>
      </div>
      <div className="note-content" onClick={() => onEdit(note)}>
        {preview || '–ù–µ—Ç —Å–æ–¥–µ—Ä–∂–∏–º–æ–≥–æ'}
      </div>
      {note.tags && note.tags.length > 0 && (
        <div className="note-tags">
          {note.tags.map(tag => (
            <span
              key={tag.id}
              className="note-tag"
              style={{ borderColor: tag.color, color: tag.color }}
            >
              {tag.name}
            </span>
          ))}
        </div>
      )}
      <div className="note-footer">
        <span className="note-date">{formatDate(note.updated_at || note.created_at)}</span>
        {note.folder_name && (
          <span className="note-folder">üìÅ {note.folder_name}</span>
        )}
      </div>
    </div>
  );
};

export default NoteCard;
