import React, { memo } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../contexts/AuthContext';
import { useTheme } from '../../contexts/ThemeContext';
import UserDropdown from './UserDropdown';
import './Header.css';

const Header = memo(({ searchQuery, onSearchChange, onCreateNote }) => {
  const { user, logout } = useAuth();
  const { theme, toggleTheme } = useTheme();
  const navigate = useNavigate();

  return (
    <header className="app-header">
      <div className="header-content">
        <div className="header-left">
          <h1 
            className="app-title" 
            onClick={() => navigate('/', { replace: false })} 
            style={{ cursor: 'pointer' }}
            title="–ü–µ—Ä–µ–π—Ç–∏ –Ω–∞ –≥–ª–∞–≤–Ω—É—é —Å—Ç—Ä–∞–Ω–∏—Ü—É"
          >
            üìù
          </h1>
        </div>
        <div className="header-center">
          <div className="search-container">
            <span className="search-icon">üîç</span>
            <input
              type="text"
              className="search-input"
              placeholder="–ü–æ–∏—Å–∫ –∑–∞–º–µ—Ç–æ–∫..."
              value={searchQuery}
              onChange={(e) => onSearchChange(e.target.value)}
            />
          </div>
        </div>
        <div className="header-right">
          <button
            className="theme-toggle"
            onClick={toggleTheme}
            title={theme === 'light' ? '–ü–µ—Ä–µ–∫–ª—é—á–∏—Ç—å –Ω–∞ —Ç–µ–º–Ω—É—é —Ç–µ–º—É' : '–ü–µ—Ä–µ–∫–ª—é—á–∏—Ç—å –Ω–∞ —Å–≤–µ—Ç–ª—É—é —Ç–µ–º—É'}
          >
            {theme === 'light' ? 'üåô' : '‚òÄÔ∏è'}
          </button>
          <UserDropdown
            user={user}
            onLogout={logout}
            onCreateNote={onCreateNote}
          />
        </div>
      </div>
    </header>
  );
});

Header.displayName = 'Header';

export default Header;
